mapper.select:
select p.prod_id,p.prod_name,p.prod_desc,oaa.default_value,p.FACTORY_PRICING from offer_prod_rel opr left join product p on opr.prod_id=p.prod_id left join (select pa.prod_id,pa.default_value from prod_attr pa left join dictionary d on pa.attr_id=d.dictionary_id where d.DICTIONARY_ID=1001) oaa on p.prod_id=oaa.prod_id
opr.offer_id=#{OFFER_ID}
and p.prod_type="M"
